import React, { useState } from 'react';
import IngredientInput from './IngredientInput';
import RecipeList from './RecipeList';
import RecipeDetail from './RecipeDetail';

import './App.css';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  console.log("Selected Recipe:", selectedRecipe);

    const searchRecipes = async (ingredient) => {
    const response = await fetch(`http://localhost:3001/recipes/search?ingredients=${ingredient}`);
    const data = await response.json();
    setRecipes(data);
  };

    return (
        <div>
            <h1>Recipe Finder</h1>
            <IngredientInput onSearch={searchRecipes} />
            <div className="content-container">
                <RecipeList recipes={recipes} onSelectRecipe={setSelectedRecipe} />
                <RecipeDetail recipe={selectedRecipe} />
            </div>
        </div>
    );
}

export default App;
